def get_info(soup, sheet, sku):
    name_element = soup.find('h1', class_='mb-f1 text-f-3xl-5xl md:w-full')
    name = name_element.get_text().strip() if name_element else ('Name not found')
    handle = name.replace(' ', '-').lower()
    
    spans = soup.findAll('span', class_='inline-block text-neutral-50')
    collection = 'NA'
    for span in spans:
        if span:
            collection = span.get_text().strip()
            if name.startswith(collection) and len(name) > len(collection):
                name = name[(len(collection)+1):]

    cat_element = soup.findAll('li', class_="text-f-base-xl after:mx-f1 after:inline-block after:content-['/'] last:after:hidden text-neutral-50 hover:text-neutral-70")
    
    category = cat_element[0].get_text().strip() if cat_element else (' ')
    sub_cat = cat_element[1].get_text().strip() if cat_element else (' ')
    weight = '0 lb'
    try:
        weight = (soup.find('span', string=lambda text: text and 'lb' in text)).get_text().strip()
    except:
        print("Error finding weight")

    try:
        dimen_element = (driver.find_element(By.XPATH, "//span[text()='Overall Dimensions']/following-sibling::span"))
    except:
        print("Couldn't find dimensions")
              
    color_element = soup.find('div', class_='text-body mb-f1 text-neutral-50 md:w-full')
    color = color_element.get_text().strip().split('•')[0] if color_element else 'NA'
    width = '0'
    depth = '0'
    height = '0'
    if dimen_element:    
        dimen = dimen_element.text.strip()
        dimen = dimen.replace('w x ', '').replace('d x ', '').replace('h', '')
        dimen = dimen.split('"')
        width = dimen[0]
        depth = dimen[1] if len(dimen) > 1 else '0'
        height = dimen[2] if len(dimen) > 2 else '0'

    #avail_element = driver.find_element(By.XPATH, "//div[@data-v-bcc67150='']//strong[text()='Ready for Immediate Shipment: ']/following-sibling::span")
    #stock = (avail_element.text) if avail_element else ('Stock NOT found')

    #arriving_element = driver.find_element(By.XPATH, "//div[@data-v-bcc67150='']//strong[text()='More Arriving Soon: ']/following-sibling::span")
    #arriving = (arriving_element.text) if arriving_element else ('Arrival date NOT found')

    descr_element = soup.find('div', class_='text-f-sm-lg')
    description = (descr_element.get_text().strip()) if descr_element else ('Description NOT found')

    cost_element = soup.find('div', class_='text-f-3xl-5xl')
    our_cost = float(cost_element.get_text().strip().replace(',', '').replace('$', '')) if cost_element else ('Cost NOT found')
    map_element = soup.find('div', class_='text-neutral-50 before:w-px')
    map_cost = float(map_element.get_text().strip()[:-4].replace(',', '').replace('$', '') + '\n') if map_element else ('MAP NOT found\n')
    
    images = soup.findAll('img', class_='aspect-square object-cover border border-solid border-neutral-30 bg-neutral-10 block w-full')

    #    sheet.append([sku, name, collection, category, sub_cat, width, depth, height, weight, color, material, stock, arriving, description, our_cost, map_cost])
    count = 1
    sheet.append([handle, name, (description + f"\n\nDIMENSIONS: {width} X {depth} X {height} | {weight[:-3]} LBS"), "FH", None, category, description, True, "Title", "Default Title", None, "Color", color, None, None, None, None, sku, float(weight[:-3])*453.592, None, 0, "continue", "manual", (our_cost*1.135)*2.2, (our_cost*1.135)*2.75, True, True, None, images[0]['src'], count, name, False, None, None, None, None, None, None, None, None, None, None, None, None, None, None, None, None, None, "lb", None, our_cost, True, None, None, "active"])
    for img in images:
        if count > 1:
            sheet.append([handle, None, None, None, None, None, None, None, None, None, None, None, None, None, None, None, None, None, None, None, None, None, None, None, None, None, None, None, img['src'], count, name, None, None, None, None, None, None, None, None, None, None, None, None, None, None, None, None, None, None, None, None, None, None, None, None, None])    
        count += 1
