def main():
    driver.get('https://fourhands.com/login')
    time.sleep(7)
    try:
        # Create a new Excel workbook and select the active worksheet
        
        # Categories to iterate over
        categories = ['bath', 'seating', 'tables-desks', 'storage', 'bed', 'lighting', 'art-studio', 'home-decor', 'outdoor']
        # Array to hold SKUs
        
        for category in categories:
            skus = []
            new_skus =[]
            workbook = Workbook()
            sheet = workbook.active
            sheet.title = f'FH Products {category}'
            sheet.append(["Handle", "Title", "Body (HTML)", "Vendor", "Product Category", "Type", "Tags", "Published", "Option1 Name", "Option1 Value", "Option1 Linked To", "Option2 Name", "Option2 Value", "Option2 Linked To", "Option3 Name", "Option3 Value", "Option3 Linked To", "Variant SKU", "Variant Grams", "Variant Inventory Tracker", "Variant Inventory Qty", "Variant Inventory Policy", "Variant Fulfillment Service", "Variant Price", "Variant Compare At Price", "Variant Requires Shipping", "Variant Taxable", "Variant Barcode", "Image Src", "Image Position", "Image Alt Text", "Gift Card", "SEO Title", "SEO Description", "Google Shopping / Google Product Category", "Google Shopping / Gender", "Google Shopping / Age Group", "Google Shopping / MPN", "Google Shopping / Condition", "Google Shopping / Custom Product", "Google Shopping / Custom Label 0", "Google Shopping / Custom Label 1", "Google Shopping / Custom Label 2", "Google Shopping / Custom Label 3", "Google Shopping / Custom Label 4", "Google: Custom Product (product.metafields.mm-google-shopping.custom_product)", "Backrest type (product.metafields.shopify.backrest-type)", "Seat type (product.metafields.shopify.seat-type)", "Variant Image", "Variant Weight Unit", "Variant Tax Code", "Cost per item", "Included / United States", "Price / United States", "Compare At Price / United States", "Status"])

            # Step 2: Visit each category URL
            driver.get(f'https://www.fourhands.com/shop/{category}/all')
            count1 = 0
            temp = True
            while temp:
                time.sleep(.2)  # Wait for the page to load
                
                # Step 3: Scroll down the page to load more items
                driver.execute_script("window.scrollTo(0, document.body.scrollHeight);")
                time.sleep(.2)  # Wait for new items to load after scrolling
                

                # Step 4: Click "View More" until all items are loaded
                try:
                    view_more_button = WebDriverWait(driver, 4).until(
                        EC.element_to_be_clickable((By.CSS_SELECTOR, 'button[data-fh-ga="load-more"]'))
                    )
                    view_more_button.click()
                    time.sleep(.2)
                    count1 += 1
                except (TimeoutException, ElementClickInterceptedException, NoSuchElementException):
                    temp = False  # Exit the loop when no more "View More" button is found
                # Step 5: Parse the page content
                soup = BeautifulSoup(driver.page_source, 'html.parser')
                product_links = soup.find_all('a', href=True, class_='block')

                for product_link in product_links:
                    sku = product_link['href'].split('/')[-1]
                    skus.append(sku)
                    skus_length = len(skus)
                    #print(f'\nNew item found {sku}. Item number {skus_length}')
            print(f'All skus found. Total: {len(skus)}')
            #print('\nAll items found... now fetching additional colors and style modes.')

            # Step 6: Iterate through all SKUs and perform the required operations
            for sku in skus:
                # Visit the product page
                driver.get(f'https://fourhands.com/product/{sku}')
                # Step 5: Parse the page content
                soup = BeautifulSoup(driver.page_source, 'html.parser')
                new_skus.append(sku)
                get_info(soup, sheet, sku)

                try:
                    # Check for the presence of the div containing the radio buttons for different styles
                    style_labels = driver.find_elements(By.CSS_SELECTOR, 'div.mb-f1.flex.overflow-x-auto input[type="radio"]')
                    if style_labels:
                        for label in style_labels:
                            # Click each label to change the style
                            driver.execute_script("arguments[0].click();", label)
                            time.sleep(.5)  # Wait for the page to load the new style

                            # Get the new SKU after clicking the label
                            new_sku = driver.current_url.split('/')[-1]
                            if new_sku not in new_skus:
                                soup = BeautifulSoup(driver.page_source, 'html.parser')
                                get_info(soup, sheet, new_sku)
                                new_skus.append(new_sku)
                            #else: 
                                #print(f'\nSku # {new_sku} already available!\n')


                except (NoSuchElementException, TimeoutException):
                    pass  # No additional styles found, continue with the current SKU
                
            # Define the file path and save the workbook to the specified directory
            directory_path = os.path.expanduser('./')#'~/OneDrive/Documents/Coding')
            if not os.path.exists(directory_path):
                os.makedirs(directory_path)
            new_workbook_path = os.path.join(directory_path, f"FH_Products_Information_{category}.xlsx")
            workbook.save(new_workbook_path)

        print(f'\nTotal number of items: {len(new_skus)}')
    finally:
        # Define the file path and save the workbook to the specified directory
        #directory_path = os.path.expanduser('~/OneDrive/Documents/Coding')
        #if not os.path.exists(directory_path):
        #    os.makedirs(directory_path)
        #new_workbook_path = os.path.join(directory_path, "FH_Products_Information.xlsx")
        #workbook.save(new_workbook_path)

        # Close the WebDriver
        driver.quit()
main()

def send_email_with_attachment(sender_email, receiver_email, email_password, attachment_path):
    # Create the email
    msg = MIMEMultipart()
    msg['From'] = sender_email
    msg['To'] = receiver_email
    msg['Subject'] = "Correctly Updated Excel File"

    body = "Please find the updated Excel file attached."
    msg.attach(MIMEText(body, 'plain'))

    # Attach the file
    with open(attachment_path, "rb") as attachment:
        part = MIMEBase('application', 'octet-stream')
        part.set_payload(attachment.read())
        encoders.encode_base64(part)
        part.add_header('Content-Disposition', f"attachment; filename= {os.path.basename(attachment_path)}")
        msg.attach(part)

    # Send the email
    server = smtplib.SMTP('smtp.gmail.com', 587)
    server.starttls()
    server.login(sender_email, email_password)
    text = msg.as_string()
    server.sendmail(sender_email, receiver_email, text)
    server.quit()

